import "./sokresultat.css";
import {useState} from "react";
import Axios from "axios";

function App() {

  const [imagesIDList, setImagesIDList] = useState([]);
  const [category, setCategory] = useState("");
  

  const getImagesID = (category) => {
    Axios.post("http://localhost:3003/imagesID",{category: category}).then((response) => {
      setImagesIDList(response.data);
      {setCategory(localStorage.getItem("Category"));}
    });
  };



  const getImageContent = imagesIDList => {
    let content = [];
    for (let i = 0; i < imagesIDList.length; i++) {
      const item = imagesIDList[i];
      console.log(item.img);
      content.push(<li><div className="image">
      <img src={`data:image/png;base64,${item}`} alt=""/>   
    </div></li>);
    }
    return content;
  }



  return (
    <div className="test">
      <h1>{category}</h1>


      {getImageContent(imagesIDList)}
      
      
      <div className="images">
        {getImagesID(category)};
        {imagesIDList.filter((item, index) => index < 3).map((val, key) => {            
            return (
            <div className="image">
              <img src={`data:image/png;base64,${val.img}`} alt=""/>   
            </div>
            );
          })}
          
          
      </div>
    </div>
  );
}

export default App;
