import "./layout.css";
import logo from "./../../images/logo.png";
import { Outlet, Link } from "react-router-dom";
import {useState} from "react";

// const toggleButton = document.getElementById('toggle-button');
// const naviList = document.getElementById('navi-list');

// toggleButton.addEventListener('click', () => {
//   naviList.classList.toggle('active');
// })
function Example() {

const [isActive, setActive] = useState("false");
const ToggleClass = () => {
  setActive(!isActive); 
  };

const Layout = () => {
  return (
    <>
      <nav className="navbar">
        <div>
          <Link to="/"><img class="primary-icon" src={logo} alt="Logo"/></Link>
        </div>
        <ul className="nav-list" id="navi-list">
          <li className="list-item">
            <Link to="/arkiv">Arkiv</Link>
          </li>
          <li className="list-item">
            <Link to="/kontakt">Kontakt</Link>
          </li>
        </ul>
        <div className={isActive ? "active" : null}>
          <div className="menu" id="toggle-button" onClick={ToggleClass}>
            <div class="menu-line"></div>
            <div class="menu-line"></div>
            <div class="menu-line"></div>
          </div>
        </div>
      </nav>

      <Outlet />
    </>
  )
};
}

export default Layout;
